#[inline]
pub fn svg_matrix_to_trans(el: &[f32; 6]) -> Transform {
    let mat = Mat4::from_cols_array(&[
        el[0], el[1], 0., 0., el[2], el[3], 0., 0., 0., 0., 1., 0., el[4], el[5], 1., 0.,
    ]);
    Transform::from_matrix(mat)
}

pub fn generate_bundle_from_asset(
    trans: &[Option<[f32; 6]>],
    paths: &[Option<&str>],
) -> Vec<(Shape, Transform)> {
    let size = Vec2::splat(50.);
    trans
        .iter()
        .copied()
        .zip(paths.iter().copied())
        .filter_map(|(a, b)| {
            if let (Some(x), Some(y)) = (a, b) {
                Some((x, y))
            } else {
                None
            }
        })
        .map(|(trans, path)| {
            let svg = shapes::SvgPathShape {
                svg_path_string: path.to_owned(),
                svg_doc_size_in_px: size,
            };
            let trans = svg_matrix_to_trans(&trans);
            (
                ShapeBuilder::with(&svg)
                    .stroke((Color::WHITE, LINE_STROKE))
                    .build(),
                Transform {
                    translation: trans.translation * Vec3::new(1., -1., 1.),
                    ..trans
                },
            )
        })
        .collect_vec()
}
